import React, { useState } from "react";

const Card = ({ books }) => {
    const [selectedCard, setSelectedCard] = useState(null);

    const toggleCard = (index) => {
        setSelectedCard(selectedCard === index ? null : index);
    };

    return (
        <>
            {books.map((book, index) => {
                const volumeInfo = book.volumeInfo;
                const thumbnail = volumeInfo.imageLinks ? volumeInfo.imageLinks.smallThumbnail : '';
                const title = volumeInfo.title;
                const authors = volumeInfo.authors ? volumeInfo.authors.join(', ') : 'Unknown Author';
                const publisher = volumeInfo.publisher || 'Unknown Publisher';
                const releaseDate = volumeInfo.publishedDate || 'Unknown Date';
                const rating = volumeInfo.averageRating || 'No Rating';
                const description = volumeInfo.description ? volumeInfo.description.substring(0, 100) + '...' : 'No Description';
                const review = volumeInfo.reviews ? volumeInfo.reviews : 'No Reviews Available';

                return (
                    <div key={book.id} className="card" onClick={() => toggleCard(index)}>
                        <img src={thumbnail} alt={title} />
                        <h3 className="title">{title}</h3>

                        {selectedCard === index && (
                            <div className="additional-info">
                                <p><strong>Author:</strong> {authors}</p>
                                <p><strong>Publisher:</strong> {publisher}</p>
                                <p><strong>Release Date:</strong> {releaseDate}</p>
                                <p><strong>Rating:</strong> {rating}</p>
                                <p><strong>Description:</strong> {description}</p>
                                <p><strong>Review:</strong> {review}</p>
                            </div>
                        )}
                    </div>
                );
            })}
        </>
    );
};

export default Card;
