import React, { useState } from "react";
import Modal from "./Modal";

const Card = ({ book }) => {
    const [show, setShow] = useState(false);
    const [bookItem, setItem] = useState();

    return (
        <>
            {book.map((item, index) => {
                const thumbnail = item.volumeInfo.imageLinks && item.volumeInfo.imageLinks.smallThumbnail;
                const title = item.volumeInfo.title;
                const authors = item.volumeInfo.authors ? item.volumeInfo.authors.join(', ') : 'Unknown Author';
                const publisher = item.volumeInfo.publisher || 'Unknown Publisher';
                const publishedDate = item.volumeInfo.publishedDate || 'Unknown Date';
                const rating = item.volumeInfo.averageRating || 'No Rating';

                if (thumbnail && title && authors) {
                    return (
                        <div key={index} className="card" onClick={() => { setShow(true); setItem(item); }}>
                            <img src={thumbnail} alt={title} />
                            <div className="bottom">
                                <h3 className="title">{title}</h3>
                                <h4>By: {authors}</h4>
                                <p>Publisher: {publisher}</p>
                                <p>Release Date: {publishedDate}</p>
                                <p>Rating: {rating} / 5</p>
                            </div>
                        </div>
                    );
                }

                return null;
            })}
            {bookItem && <Modal show={show} item={bookItem} onClose={() => setShow(false)} />}
        </>
    );
}

export default Card;
