import React from 'react';

const Modal = ({ show, item, onClose }) => {
    if (!show) {
        return null;
    }

    const thumbnail = item.volumeInfo.imageLinks && item.volumeInfo.imageLinks.smallThumbnail;
    const title = item.volumeInfo.title;
    const authors = item.volumeInfo.authors ? item.volumeInfo.authors.join(', ') : 'Unknown Author';
    const publisher = item.volumeInfo.publisher || 'Unknown Publisher';
    const publishedDate = item.volumeInfo.publishedDate || 'Unknown Date';
    const rating = item.volumeInfo.averageRating || 'No Rating';
    const genre = item.volumeInfo.categories ? item.volumeInfo.categories.join(', ') : 'Unknown Genre';
    const review = item.volumeInfo.description || 'No Description Available';

    return (
        <div className="overlay">
            <div className="overlay-inner">
                <button className="close" onClick={onClose}><i className="fas fa-times"></i></button>
                <div className="inner-box">
                    <img src={thumbnail} alt={title} />
                    <div className="info">
                        <h1>{title}</h1>
                        <h3>By: {authors}</h3>
                        <h4>Published by: {publisher} on {publishedDate}</h4>
                        <h4>Genre: {genre}</h4>
                        <h4>Rating: {rating} / 5</h4>
                        <p className="review">{review}</p>
                        <a href={item.volumeInfo.previewLink}><button>More</button></a>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Modal;
